userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* linkhint boxes */
  background: -webkit-gradient(
    linear,
    left top,
    left bottom,
    color-stop(0%, #301934),
    color-stop(100%, #783475)
  );
  border: 1px solid #E3BE23;
}

div > .vimiumHintMarker span {
  /* linkhint text */
  color: black;
  font-weight: bold;
  font-size: 12px;
}

div > .vimiumHintMarker > .matchingCharacter {
  /* styles for matching character, if needed */
}
